sys_script_include.script:
var RandomMessage = Class.create();
RandomMessage.prototype = {
	initialize: function() {
	},

	getMessage : function(user, points, total, rank) {
		var random_messages = [
			'Congrats ' + user + ' you now have ' + points + ' points',
			'Good work ' + user + ' you now have ' + points + ' points',
			'Good going ' + user + ' you now have ' + points + ' points',
			'Way to help out ' + user + ' you now have ' + points + ' points',
			'Give a hear-hear for ' + user + ' you now have ' + points + ' points',
			'Who has two thumbs and one more point? ' + user + ' does.  You now have ' + points + ' points',
			'Thank you ' + user + ' you now have ' + points + ' points',
			'Helping the community, one point at a time! ' + user + ' now has ' + points + ' points',
			'Who\'s awesome? ' + user + ' is awesome! You now have ' + points + ' points'
		];
		var x = getRandomInt(0,random_messages.length-1);
		//var x = 1;
		var bodyString = random_messages[x];

		bodyString += ' (' + total + ' total)';
		var commendation = '';
		if (rank == 1) commendation = ':first_place_medal:';
		else if (rank == 2) commendation = ':second_place_medal:';
		else if (rank == 3) commendation = ':third_place_medal:';
		else if (rank >= 4 && rank <= 10) commendation = ':star:';
		bodyString += ' ' + commendation;
		return bodyString;

		function getRandomInt(min, max) {
			return Math.floor(Math.random() * (max - min + 1)) + min;
		}
	},




	type: 'RandomMessage'
};
